declare const fabric: any;

const BG1 = 'data:image/png;base64,';
const BG2 = 'data:image/png;base64,';
const BG3 = 'data:image/png;base64,';
const BG4 = 'data:image/png;base64,';
const BG5 = 'data:image/png;base64,';
const BG6 = 'data:image/png;base64,';

const BACKGROUNDS: any = {
  'bg-1': BG1,
  'bg-2': BG2,
  'bg-3': BG3,
  'bg-4': BG4,
  'bg-5': BG5,
  'bg-6': BG6,
}
function getBG(bg: string) {
  return BACKGROUNDS[bg];
}

export function downloadCanvas(canvas: any, bg: string, bgColor: string, bgSize: number, bgOpacity: number) {
  canvas.clone((clonedCanvas: any) => {
    if (bg) {
      fabric.Image.fromURL(getBG(bg), function (img: any) {
        const patternSourceCanvas = new fabric.StaticCanvas();
        patternSourceCanvas.setWidth(clonedCanvas.width * (bgSize / 100));
        patternSourceCanvas.setHeight(clonedCanvas.height * (bgSize / 100));
        img.set('opacity', bgOpacity);
        img.scaleToWidth(patternSourceCanvas.width);
        patternSourceCanvas.add(img)
        patternSourceCanvas.setBackgroundColor(bgColor, patternSourceCanvas.renderAll.bind(patternSourceCanvas));

        clonedCanvas.backgroundColor = new fabric.Pattern({
          source: patternSourceCanvas.getElement(),
          repeat: 'repeat'
        });
        clonedCanvas.renderAll();
        download(clonedCanvas);
      });
    } else {
      clonedCanvas.backgroundColor = bgColor;
      clonedCanvas.renderAll();
      download(clonedCanvas);
    }
  });
}

function download(canvas: any) {
  const dataURL = canvas.toDataURL({
    width: canvas.width,
    height: canvas.height,
    left: 0,
    top: 0,
    format: 'png',
  });
  const link = document.createElement('a');
  link.download = 'whiteboard.png';
  link.href = dataURL;
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
}
